liquidNav.css:
.liquid-nav {
  position: fixed;
  top: 0;
  width: 100%;
  height: 80px;
  z-index: 1000;
  overflow: hidden;
}

.nav-background {
  position: absolute;
  inset: 0;
  background: linear-gradient(90deg, #667eea, #764ba2, #f093fb, #667eea);
  background-size: 300% 100%;
  animation: gradient-flow 8s ease infinite;
  filter: blur(40px);
  opacity: 0.8;
}

.nav-content {
  position: relative;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 5vw;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-bottom: 1px solid rgba(255, 255, 255, 0.2);
}

.nav-menu {
  display: flex;
  list-style: none;
  gap: 3rem;
}

.nav-menu a {
  color: white;
  text-decoration: none;
  position: relative;
  padding: 0.5rem 1rem;
  transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.nav-menu a:hover {
  transform: translateY(-2px) scale(1.1);
  filter: drop-shadow(0 10px 20px rgba(255, 255, 255, 0.3));
}

.nav-indicator {
  position: absolute;
  bottom: 0;
  height: 3px;
  background: linear-gradient(90deg, #00f2fe, #4facfe);
  border-radius: 3px;
  transition: all 0.4s cubic-bezier(0.68, -0.55, 0.265, 1.55);
}

@keyframes gradient-flow {
  0%, 100% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
}

morphingHero.css:
.morphing-hero {
  position: relative;
  min-height: 100vh;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #667eea, #764ba2, #f093fb, #feca57);
  background-size: 400% 400%;
  animation: gradient-shift 20s ease infinite;
}

.hero-blob {
  position: absolute;
  filter: blur(40px);
  opacity: 0.7;
  animation: morph 12s ease-in-out infinite;
}

.blob-1 {
  width: 500px;
  height: 500px;
  background: linear-gradient(45deg, #00f2fe, #4facfe);
  top: -150px;
  left: -150px;
  border-radius: 40% 60% 70% 30% / 40% 50% 60% 50%;
}

.blob-2 {
  width: 400px;
  height: 400px;
  background: linear-gradient(45deg, #fc466b, #3f5efb);
  bottom: -100px;
  right: -100px;
  border-radius: 70% 30% 50% 50% / 30% 30% 70% 70%;
  animation-delay: 4s;
}

.blob-3 {
  width: 300px;
  height: 300px;
  background: linear-gradient(45deg, #feca57, #48c9b0);
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  border-radius: 30% 70% 70% 30% / 30% 60% 40% 70%;
  animation-delay: 8s;
}

.hero-content {
  position: relative;
  z-index: 10;
  text-align: center;
  color: white;
}

.hero-title {
  font-size: clamp(3rem, 12vw, 10rem);
  font-weight: 900;
  letter-spacing: -0.05em;
  background: linear-gradient(90deg, #fff, #00f2fe, #fff);
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  animation: gradient-text 8s ease infinite;
  text-shadow: 0 0 40px rgba(255, 255, 255, 0.5);
}

@keyframes morph {
  0%, 100% { border-radius: 40% 60% 70% 30% / 40% 50% 60% 50%; transform: rotate(0deg); }
  25% { border-radius: 70% 30% 50% 50% / 30% 30% 70% 70%; transform: rotate(90deg); }
  50% { border-radius: 30% 70% 70% 30% / 30% 60% 40% 70%; transform: rotate(180deg); }
  75% { border-radius: 60% 40% 30% 70% / 60% 30% 70% 40%; transform: rotate(270deg); }
}

liquidCard.css:
.liquid-card {
  position: relative;
  width: 350px;
  height: 450px;
  border-radius: 30% 70% 70% 30% / 30% 60% 40% 70%;
  overflow: hidden;
  cursor: pointer;
  animation: card-morph 10s ease-in-out infinite;
}

.card-background {
  position: absolute;
  inset: -50%;
  background: linear-gradient(45deg, #667eea, #764ba2, #f093fb);
  animation: rotate 20s linear infinite;
  filter: blur(30px);
}

.card-content {
  position: relative;
  height: 100%;
  padding: 3rem;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px) saturate(180%);
  border: 1px solid rgba(255, 255, 255, 0.3);
  border-radius: inherit;
  display: flex;
  flex-direction: column;
  justify-content: center;
  color: white;
  transition: all 0.6s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.liquid-card:hover .card-content {
  background: rgba(255, 255, 255, 0.2);
  transform: scale(1.05) translateZ(50px);
}

.card-glow {
  position: absolute;
  inset: -50%;
  background: radial-gradient(circle, rgba(255, 255, 255, 0.3) 0%, transparent 70%);
  opacity: 0;
  transition: opacity 0.6s ease;
}

.liquid-card:hover .card-glow {
  opacity: 1;
}

@keyframes card-morph {
  0%, 100% { border-radius: 30% 70% 70% 30% / 30% 60% 40% 70%; }
  33% { border-radius: 70% 30% 30% 70% / 70% 30% 70% 30%; }
  66% { border-radius: 30% 70% 30% 70% / 30% 70% 30% 70%; }
}

@keyframes rotate {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

liquidButton.css:
.liquid-button {
  position: relative;
  padding: 1.5rem 3rem;
  font-size: 1.2rem;
  font-weight: 600;
  color: white;
  background: transparent;
  border: 2px solid rgba(255, 255, 255, 0.3);
  border-radius: 50px;
  overflow: hidden;
  cursor: pointer;
  transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.button-liquid {
  position: absolute;
  top: 50%;
  left: 50%;
  width: 0;
  height: 0;
  background: linear-gradient(45deg, #00f2fe, #4facfe);
  border-radius: 50%;
  transform: translate(-50%, -50%);
  transition: all 0.6s cubic-bezier(0.68, -0.55, 0.265, 1.55);
  z-index: -1;
}

.liquid-button:hover .button-liquid {
  width: 300%;
  height: 300%;
}

.liquid-button:hover {
  color: #001F3F;
  border-color: transparent;
  transform: translateY(-3px) scale(1.05);
  box-shadow: 0 10px 30px rgba(0, 242, 254, 0.4);
}

.button-text {
  position: relative;
  z-index: 1;
}

particleField.css:
.particle-field {
  position: fixed;
  inset: 0;
  overflow: hidden;
  z-index: -1;
}

.particle {
  position: absolute;
  width: var(--size, 10px);
  height: var(--size, 10px);
  background: radial-gradient(circle, rgba(255, 255, 255, 0.8) 0%, transparent 70%);
  border-radius: 50%;
  filter: blur(1px);
  animation: float calc(20s * var(--i)) linear infinite;
  opacity: 0;
}

.particle:nth-child(odd) {
  --size: 15px;
  left: calc(12.5% * var(--i));
  animation-delay: calc(-2s * var(--i));
}

.particle:nth-child(even) {
  --size: 8px;
  right: calc(12.5% * var(--i));
  animation-delay: calc(-3s * var(--i));
}

@keyframes float {
  0% {
    transform: translateY(100vh) rotate(0deg);
    opacity: 0;
  }
  10% {
    opacity: 1;
  }
  90% {
    opacity: 1;
  }
  100% {
    transform: translateY(-100vh) rotate(360deg);
    opacity: 0;
  }
}

meshGradient.css:
.mesh-gradient {
  position: fixed;
  inset: 0;
  overflow: hidden;
  z-index: -2;
  background: #0a0a0a;
}

.gradient-layer {
  position: absolute;
  width: 150%;
  height: 150%;
  opacity: 0.6;
  filter: blur(100px) saturate(150%);
}

.layer-1 {
  background: radial-gradient(circle at 20% 80%, #667eea 0%, transparent 50%),
              radial-gradient(circle at 80% 20%, #764ba2 0%, transparent 50%);
  animation: drift 25s ease-in-out infinite;
}

.layer-2 {
  background: radial-gradient(circle at 50% 50%, #f093fb 0%, transparent 40%),
              radial-gradient(circle at 20% 50%, #feca57 0%, transparent 40%);
  animation: drift 20s ease-in-out infinite reverse;
}

.layer-3 {
  background: radial-gradient(circle at 80% 80%, #48c9b0 0%, transparent 50%),
              radial-gradient(circle at 20% 20%, #00f2fe 0%, transparent 50%);
  animation: drift 30s ease-in-out infinite;
  animation-delay: -5s;
}

@keyframes drift {
  0%, 100% { transform: translate(-10%, -10%) rotate(0deg); }
  33% { transform: translate(10%, -20%) rotate(120deg); }
  66% { transform: translate(-20%, 10%) rotate(240deg); }
}

glassModal.css:
.glass-modal-overlay {
  position: fixed;
  inset: 0;
  background: linear-gradient(135deg, rgba(0, 0, 0, 0.7) 0%, rgba(0, 0, 0, 0.9) 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 2000;
  backdrop-filter: blur(5px);
}

.glass-modal {
  position: relative;
  width: min(90%, 500px);
  border-radius: 30px;
  overflow: hidden;
  animation: modal-appear 0.6s cubic-bezier(0.68, -0.55, 0.265, 1.55);
}

.modal-glow {
  position: absolute;
  inset: -100px;
  background: conic-gradient(from 180deg, #667eea, #764ba2, #f093fb, #feca57, #48c9b0, #667eea);
  animation: rotate 4s linear infinite;
  filter: blur(50px);
  opacity: 0.5;
}

.modal-content {
  position: relative;
  padding: 4rem;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px) saturate(180%);
  border: 1px solid rgba(255, 255, 255, 0.3);
  border-radius: inherit;
  color: white;
}

.modal-close {
  position: absolute;
  top: 1rem;
  right: 1rem;
  width: 40px;
  height: 40px;
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 50%;
  color: white;
  font-size: 1.5rem;
  cursor: pointer;
  transition: all 0.3s ease;
}

.modal-close:hover {
  background: rgba(255, 255, 255, 0.2);
  transform: rotate(90deg);
}

@keyframes modal-appear {
  from {
    opacity: 0;
    transform: scale(0.5) rotate(-10deg);
  }
  to {
    opacity: 1;
    transform: scale(1) rotate(0);
  }
}

liquidCursor.css:
body {
  cursor: none;
}

.cursor-blob {
  position: fixed;
  width: 40px;
  height: 40px;
  background: linear-gradient(45deg, #667eea, #764ba2);
  border-radius: 50%;
  pointer-events: none;
  z-index: 9999;
  mix-blend-mode: screen;
  transition: transform 0.2s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.cursor-trail {
  position: fixed;
  width: 20px;
  height: 20px;
  background: rgba(118, 75, 162, 0.5);
  border-radius: 50%;
  pointer-events: none;
  z-index: 9998;
  transition: all 0.5s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.cursor-blob.hover {
  transform: scale(2);
  background: linear-gradient(45deg, #f093fb, #feca57);
}

/* JavaScript required for cursor following */

liquidCursor.javascript:
const cursorBlob = document.querySelector('.cursor-blob');
const cursorTrail = document.querySelector('.cursor-trail');

document.addEventListener('mousemove', (e) => {
  cursorBlob.style.left = e.clientX - 20 + 'px';
  cursorBlob.style.top = e.clientY - 20 + 'px';

  setTimeout(() => {
    cursorTrail.style.left = e.clientX - 10 + 'px';
    cursorTrail.style.top = e.clientY - 10 + 'px';
  }, 100);
});

document.querySelectorAll('a, button').forEach(elem => {
  elem.addEventListener('mouseenter', () => cursorBlob.classList.add('hover'));
  elem.addEventListener('mouseleave', () => cursorBlob.classList.remove('hover'));
});
